import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Library from "../library/library";
import Feed from "../feed/feed";
import Player from "../player/player";
import Favourites from "../favourites/favourites";
import Trending from "../trending/trending";
import Login from "../login/LoginForm";
import Playlist from "../Playlists/Playlist";
import Layout from "../components/Layout"; // You'll need to create this component
import NotFound from "../components/NotFound"; // You'll need to create this component

// A simple auth check function (you should implement proper auth logic)
const isAuthenticated = () => !!localStorage.getItem('token');

// Protected route component
const ProtectedRoute = ({ children }) => {
    if (!isAuthenticated()) {
        return <Navigate to="/login" replace />;
    }
    return children;
};

export default function Home() {
    return (
        <Router>
            <Routes>
                <Route path="/login" element={<Login />} />
                
                <Route path="/" element={
                    <ProtectedRoute>
                        <Layout>
                            <Library />
                        </Layout>
                    </ProtectedRoute>
                } />
                
                <Route path="/library" element={
                    <ProtectedRoute>
                        <Layout>
                            <Library />
                        </Layout>
                    </ProtectedRoute>
                } />
                
                <Route path="/feed" element={
                    <ProtectedRoute>
                        <Layout>
                            <Feed />
                        </Layout>
                    </ProtectedRoute>
                } />
                
                <Route path="/trending" element={
                    <ProtectedRoute>
                        <Layout>
                            <Trending />
                        </Layout>
                    </ProtectedRoute>
                } />
                
                <Route path="/player" element={
                    <ProtectedRoute>
                        <Layout>
                            <Player />
                        </Layout>
                    </ProtectedRoute>
                } />
                
                <Route path="/favourites" element={
                    <ProtectedRoute>
                        <Layout>
                            <Favourites />
                        </Layout>
                    </ProtectedRoute>
                } />
                
                <Route path="/Playlists/:id" element={
                    <ProtectedRoute>
                        <Layout>
                            <Playlist />
                        </Layout>
                    </ProtectedRoute>
                } />
                
                <Route path="*" element={<NotFound />} />
            </Routes>
        </Router>
    );
}
