import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

// Spotify authorization constants
const authEndpoint = "https://accounts.spotify.com/authorize?";
const clientId = "414414d8645b4746a08bc76b9abd6809";
const redirectUri = "http://localhost:3000/profile"; // Update this to match your Spotify app settings
const scopes = ["user-library-read", "user-read-private", "playlist-read-private"];

const loginEndpoint = `${authEndpoint}client_id=${clientId}&redirect_uri=${encodeURIComponent(redirectUri)}&scope=${encodeURIComponent(scopes.join(" "))}&response_type=token&show_dialog=true`;

const UserProfile = () => {
  const [user, setUser] = useState(null);
  const [playlists, setPlaylists] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      try {
        const response = await axios.get('http://localhost:5000/api/user', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setUser(response.data);
        // Fetch user playlists here if your API supports it
        // setPlaylists(response.data.playlists);
      } catch (err) {
        console.error('Error fetching user data:', err);
        setError('Failed to load user data');
      } finally {
        setIsLoading(false);
      }
    };

    fetchUserData();
  }, [navigate]);

  const handleSpotifyConnect = () => {
    window.location.href = loginEndpoint;
  };

  if (isLoading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="user-profile">
      <h1>Welcome, {user?.username}</h1>
      <div className="user-info">
        <p>Email: {user?.email}</p>
        {/* Add more user info here */}
      </div>
      <h2>Your Playlists</h2>
      {playlists.length > 0 ? (
        <ul>
          {playlists.map(playlist => (
            <li key={playlist.id}>{playlist.name}</li>
          ))}
        </ul>
      ) : (
        <p>No playlists found. Connect to Spotify to see your playlists.</p>
      )}
      <button onClick={handleSpotifyConnect}>Connect to Spotify</button>
    </div>
  );
};

export default UserProfile;
