import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Library from "../library/library";
import Feed from "../feed/feed";
import Player from "../player/player";
import Favourites from "../favourites/favourites";
import Trending from "../trending/trending";
import Login from "../login/LoginForm";
import Playlist from "../Playlists/Playlist";
import ProtectedRoute from "../../components/protectedRoutes";
import Register from "../register/register";
import NotFound from "../../components/notFound/notFound";

export default function Home() {
    const [token, setToken] = useState("");

    useEffect(() => {
        const hash = window.location.hash;
        window.location.hash = "";
        
        let storedToken = window.localStorage.getItem("token");

        if (!storedToken && hash) {
            storedToken = hash.substring(1).split("&").find(elem => elem.startsWith("access_token")).split("=")[1];
            window.localStorage.setItem("token", storedToken);
        }

        setToken(storedToken);
    }, []);

    return (
        <Router>
            <div className="main-body">
                <Routes>
                    <Route path="/" element={token ? <Navigate replace to="/library" /> : <Navigate replace to="/login" />} />
                    <Route path="/login" element={token ? <Navigate replace to="/library" /> : <Login />} />
                    <Route path="/register" element={<Register />} />
                    <Route path="/library" element={<ProtectedRoute token={token}><Library /></ProtectedRoute>} />
                    <Route path="/feed" element={<ProtectedRoute token={token}><Feed /></ProtectedRoute>} />
                    <Route path="/trending" element={<ProtectedRoute token={token}><Trending /></ProtectedRoute>} />
                    <Route path="/player" element={<ProtectedRoute token={token}><Player /></ProtectedRoute>} />
                    <Route path="/favourites" element={<ProtectedRoute token={token}><Favourites /></ProtectedRoute>} />
                    <Route path="/playlists/:id" element={<ProtectedRoute token={token}><Playlist /></ProtectedRoute>} />
                    <Route path="*" element={<NotFound />} />
                </Routes>
            </div>
        </Router>
    );
}
