import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Library = () => {
  const [libraryData, setLibraryData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchLibraryData = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          setError('No token found. Please log in.');
          return;
        }

        const response = await axios.get('https://api.spotify.com/v1/playlists/3cEYpjA9oz9GiPac4AsH4n', {
          headers: { Authorization: `Bearer ${token}` }
        });
        
        setLibraryData(response.data);
      } catch (error) {
        console.error('Error fetching library data:', error);
        setError('Failed to fetch library data. Please try again.');
      }
    };

    fetchLibraryData();
  }, []);

  if (error) {
    return <div>{error}</div>;
  }

  if (!libraryData) {
    return <div>Loading...</div>;
  }

  const renderTracks = () => {
    if (!libraryData.tracks) return <div>No tracks found.</div>;
    return (
      <div>
        <h2>Tracks</h2>
        <ul>
          {libraryData.tracks.items.map(track => (
            <li key={track.id}>{track.name}</li>
          ))}
        </ul>
      </div>
    );
  };

  const renderAlbums = () => {
    if (!libraryData.albums) return <div>No albums found.</div>;
    return (
      <div>
        <h2>Albums</h2>
        <ul>
          {libraryData.albums.items.map(album => (
            <li key={album.id}>{album.name}</li>
          ))}
        </ul>
      </div>
    );
  };

  const renderPlaylists = () => {
    if (!libraryData.playlists) return <div>No playlists found.</div>;
    return (
      <div>
        <h2>Playlists</h2>
        <ul>
          {libraryData.playlists.items.map(playlist => (
            <li key={playlist.id}>{playlist.name}</li>
          ))}
        </ul>
      </div>
    );
  };

  return (
    <div>
      <h1>Your Library</h1>
      {renderTracks()}
      {renderAlbums()}
      {renderPlaylists()}
    </div>
  );
};

export default Library;
